function moreFunction(){document.querySelector(".more").classList.toggle("active")}function loginFunction(){document.getElementById("login").classList.toggle("active")}function menuFunction(){const e=document.getElementById("menu-toggle"),t=document.getElementById("hamburger");e.classList.toggle("active"),t.classList.toggle("active")}function toggleMe(){const e=document.querySelectorAll(".body, .menu-con");for(const t of e)t.classList.toggle("active")}const elements=document.getElementsByClassName("more"),myFunction=function(){this.querySelector(".more__action").parentNode.classList.toggle("active")};for(var i=0;i<elements.length;i++)elements[i].addEventListener("click",myFunction,!1);